public class TestVariable {
    public static void main(String[] args) {
        Base b = new Sub();
        b.display();
    }
}

class Base {
    int a = 1;

    public void display() {
        System.out.println(this.a);
    }
}

class Sub extends Base {
    int a = 2;

//    如果注释屌 Sub 中的 display ，b.display()打印 1 ，否则打印 2 ，为什么？
    public void display() {
        System.out.println(this.a);
    }
}
