}

func (q *pq) get() int {
	if len(q.arr) > 0 {
		num := q.arr[0]
		q.arr = q.arr[1:]
		return num
	}
	panic("arr is empty!!!")
}

func (q *pq) getSize() int {
	return len(q.arr)
}

func b_s(arr []int, target_num int) int {
	if len(arr) == 0 {
		return 0
	} else {
		left, right := 0, len(arr)-1
		for {
			if right-left <= 1 {
				if target_num <= arr[left] {
					return left
				} else if target_num <= arr[right] {
					return right
				} else {
					return right + 1
				}
			} else {
				mid := (left + right) / 2
				if arr[mid] == target_num {
					return mid
				} else if arr[mid] < target_num {
					left = mid
				} else {
					right = mid
				}
			}
		}
	}
}

func main() {
	s := &pq{arr: make([]int, 0)}
	s.put(20)
	s.put(3)
	s.put(30)
	fmt.Println(s.arr)
}

