void quickSort(int left, int right, vector<int>& arr) {
    int i = left, j = right;
    int pivot = arr[left];
    while(i < j)
    {
        if(i < j)
        while(arr[i] < pivot) i++;
        while(arr[j] > pivot) j--;
        if (i < j)
        {
            swap(arr[i], arr[j]);
        }
    }
    if(left < j)
        quickSort(left, j, arr);
